mapper.select:
SELECT M_ID mId, PASSWORD, M_NAME mName, EMAIL, PHONE, M_COUNT mCount, M_POINT mPoint, USERTYPE, DELETED, left(REG_DATE, 10) regDate
FROM rnh2.MEMBER
WHERE M_ID = #{ mId }

mapper.update:
UPDATE rnh2.MEMBER
SET
PASSWORD = #{ password }, M_NAME = #{ mName }, EMAIL = #{ email }, PHONE = #{ phone }
WHERE M_ID = #{ mId }

mapper.select:
SELECT M_ID mId, POINT, left(REG_DATE, 10) regDate, CONTENT
FROM rnh2.MEMBER_HISTORY 
WHERE M_ID = #{ mId }
