mapper.update:

	UPDATE QNA
	SET deleted = '1', TITLE = #{title}
	WHERE N_NO = #{nNo}


	UPDATE QNA
	SET TITLE = #{title}, CONTENT = #{content}
	WHERE Q_NO = #{qNo}

mapper.select:
	
		SELECT q_no qNo, title title, content content, m_id mId, reg_date regDate

		FROM QNA
		
		WHERE q_no = #{qNo}
